mapper.sql:
SELECT DISTINCT C.VR_NO ,D.TT_NO ,A.SS_NAME AS SS_NO ,C.VR_STATUS AS VR_STATUS ,B.NAME AS CODE ,G.NAME AS VR_FIELD ,TO_CHAR(C.VR_DATE, 'YYYY-MM-DD') AS VR_DATE ,TO_CHAR(C.VR_INSERTDATE, 'YYYY-MM-DD') AS VR_INSERTDATE ,F.TIME AS VR_TIME FROM S_MEMBER A, CODE_TABLE B, VISITCOUNSEL_RSV C JOIN EDU_SCHEDULE D ON (C.TT_NO = D.TT_NO), CODE_VISITTIMETABLE F, CODE_TABLE G WHERE C.SS_NO = A.SS_NO AND B.CODE = C.VR_STATUS AND G.CODE = C.VR_FIELD AND F.TIMENO = C.VR_TIME
G.NAME=#{keyword}
B.CODE=#{search}

mapper.select:
SELECT DISTINCT VR_NO, RNUM ,TT_NO ,SS_NO ,VR_STATUS ,CODE ,VR_FIELD ,VR_DATE ,VR_INSERTDATE ,VR_TIME FROM (SELECT LIST.*, ROWNUM AS RNUM FROM ( AND C.TT_NO = #{ttNo} ORDER BY VR_NO
DESC ASC
) LIST)
RNUM BETWEEN #{startRow} AND #{endRow}
ORDER BY RNUM

mapper.update:
UPDATE VISITCOUNSEL_RSV
SET VR_STATUS = #{vrStatus}
WHERE TT_NO = #{ttNo}
AND VR_NO = #{vrNo}
AND VR_TIME = #{vrTime}
AND VR_DATE = #{vrDate}
