mapper.select:
SELECT * FROM (SELECT A.SS_NO SSNO ,(SELECT E.NAME FROM CODE_TABLE E WHERE E.CODE=A.SS_GRADE) SSGRADE ,A.SS_NAME SSNAME ,(CASE WHEN A.SS_GRADE='FM' THEN (SELECT AA.FM_CLASS FROM FM_MEMBER AA, S_MEMBER BB WHERE AA.SS_NO = BB.SS_NO AND AA.SS_NO=A.SS_NO) WHEN A.SS_GRADE='JR' THEN (SELECT AA.JR_CLASS FROM JR_MEMBER AA, S_MEMBER BB WHERE AA.SS_NO = BB.SS_NO AND AA.SS_NO=A.SS_NO) WHEN A.SS_GRADE='SR' THEN (SELECT AA.SR_CLASS FROM SR_MEMBER AA, S_MEMBER BB WHERE AA.SS_NO = BB.SS_NO AND AA.SS_NO=A.SS_NO) END) AS SSCLASS ,(CASE WHEN A.SS_GRADE='FM' THEN (SELECT AA.FM_NUM FROM FM_MEMBER AA, S_MEMBER BB WHERE AA.SS_NO = BB.SS_NO AND AA.SS_NO=A.SS_NO) WHEN A.SS_GRADE='JR' THEN (SELECT AA.JR_NUM FROM JR_MEMBER AA, S_MEMBER BB WHERE AA.SS_NO = BB.SS_NO AND AA.SS_NO=A.SS_NO) WHEN A.SS_GRADE='SR' THEN (SELECT AA.SR_NUM FROM SR_MEMBER AA, S_MEMBER BB WHERE AA.SS_NO = BB.SS_NO AND AA.SS_NO=A.SS_NO) END) AS SSNUM ,(SELECT CD_URL FROM CAREER_DESIGN B WHERE A.SS_NO = B.SS_NO AND CD_VALUE='JA' AND A.SS_GRADE=B.CD_GRADE) JAURL ,(SELECT CD_URL FROM CAREER_DESIGN B WHERE A.SS_NO = B.SS_NO AND CD_VALUE='JI' AND A.SS_GRADE=B.CD_GRADE) JIURL ,(SELECT CD_URL FROM CAREER_DESIGN B WHERE A.SS_NO = B.SS_NO AND CD_VALUE='IH' AND A.SS_GRADE=B.CD_GRADE) IHURL ,(SELECT CD_URL FROM CAREER_DESIGN B WHERE A.SS_NO = B.SS_NO AND CD_VALUE='VA' AND A.SS_GRADE=B.CD_GRADE) VAURL ,(SELECT CD_URL FROM CAREER_DESIGN B WHERE A.SS_NO = B.SS_NO AND CD_VALUE='CM' AND A.SS_GRADE=B.CD_GRADE) CMRUL ,CEIL(ROW_NUMBER() OVER(ORDER BY A.SS_NO)/#{listSize}) PAGENO ,COUNT(A.SS_NO) OVER() AS TOTALCOUNT FROM S_MEMBER A
,FM_MEMBER B
,JR_MEMBER B
,SR_MEMBER B
WHERE A.SS_DELETEYN = 'Y' AND A.SS_AUTHORITY = '1'
AND A.SS_GRADE=#{ssGrade}
AND B.FM_CLASS=#{ssClass}
AND B.JR_CLASS=#{ssClass}
AND B.SR_CLASS=#{ssClass}
AND A.SS_NAME LIKE '%'|| #{ssName} ||'%'
)ABC WHERE ABC.PAGENO = #{pageNo}
