mapper.select:
SELECT B.* FROM( SELECT NP_NO, NP_TITLE, NP_CONTENTS, NP_CENTER, NP_IMAGE, NP_RSDATE, NP_REDATE, NP_PSDATE, NP_PEDATE, NP_OBJECT, NP_PERSONNEL, A.TT_NO, (SELECT TT_NAME FROM T_MEMBER WHERE TT_NO=A.TT_NO) TT_NAME, NP_IO, ACODE, NP_DELETEYN, NP_INSERTDATE, NP_UPDATEDATE, CEIL(ROW_NUMBER() OVER(ORDER BY NP_NO DESC)/#{listSize}) PAGENO, COUNT(NP_NO) OVER() AS TOTALCOUNT FROM NONPROGRAM A WHERE NP_DELETEYN='Y'
)B WHERE PAGENO = #{pageNo}
AND NP_TITLE LIKE '%'|| #{npTitle} ||'%'
AND NP_CONTENTS LIKE '%'|| #{npContents} ||'%'
