mapper.sql:
SELECT A.OA_NO OA_NO, (SELECT B.NAME FROM CODE_TABLE B WHERE B.CODE = A.OA_STATUS)AS OA_STATUS, (SELECT B.NAME FROM CODE_TABLE B WHERE B.CODE = A.OA_FIELD) AS OA_FIELD, A.OA_TITLE OA_TITLE, (SELECT C.SS_NAME FROM S_MEMBER C WHERE A.SS_NO = C.SS_NO) AS SS_NAME, (SELECT C.TT_NAME FROM T_MEMBER C WHERE A.TT_NO = C.TT_NO) AS TT_NAME, A.OA_INSERTDATE OA_INSERTDATE FROM ONLINE_APPLICATION A WHERE A.OA_DELETEYN = 'Y' AND A.SS_NO = #{ssNo}
OA_TITLE LIKE '%' || #{keyword} || '%'
AND OA_FIELD LIKE '%' || #{keyword} || '%'

mapper.select:
SELECT OA_NO, OA_STATUS, OA_FIELD, OA_TITLE, SS_NAME, TT_NAME, TO_CHAR(OA_INSERTDATE, 'YYYY-MM-DD') AS OA_INSERTDATE FROM ( SELECT range.*, ROWNUM AS RNUM FROM (
ORDER BY OA_NO
ASC
DESC
) range )
RNUM BETWEEN #{startRow} AND #{endRow}
